import React from 'react';
import {UserContext, AuthStatus} from '../Context/context';

function Balance() {
    const ctx = React.useContext(UserContext);
    const AuthenticationStatus = React.useContext(AuthStatus);
    const isLoggedIn = AuthenticationStatus.LoginStatus

    const users= ctx.users;
    const acc = ctx.account[0].email;
    const index = () => {
        for (let i = 0; i < users.length; i++) {
        const element = users[i];
        if (element.email === acc ){
           return i;            
        };
        return false
        };
    }

    return (
        isLoggedIn ?
        (
        <div className="container">
            <h1>{users[index].name}'s Account Balance </h1><br/>
            <h5>{users[index].balance}</h5>
        </div>
        ):(
        <div className="container">
        <h1>You are currently not logged in!</h1><br/>
        <p>Please login or register for an account first...</p>
        </div>
        )
    );
};

export default Balance;
