import React from 'react';
import { HashRouter, Routes, Route } from "react-router-dom";
import { Home, AllData, NavBar, CreateAccount, Login, Deposit, Withdraw, Balance } from './Components';
import { UserContext, AuthStatus } from './Context/context';
import './App.css';

function Spa() {

  return (
      <HashRouter>
          <UserContext.Provider value={{  users:[{name: 'Mathias', email: 'mk@intutec.io', password: 'secret', balance: 100}],
                                          account:[{name: '', email: '', balance: 0}]
                                        }} >
            <AuthStatus.Provider value={{LoginStatus: false}}>
            <Consumer>
            { value => (
            value.LoginStatus == true ? (
                <NavLoggedIn />
            ):(
            <NavPublic />
            )
            )}
    </Consumer>
            <NavBar />
            <Routes>
                  <Route path="/" exact element={<Home />} />
                  <Route path="/login/" element={<Login />} />
                  <Route path="/withdraw/" element={<Withdraw />} />
                  <Route path="/deposit/" element={<Deposit />} />
                  <Route path="/balance/" element={<Balance />} />
                  <Route path="/createaccount/" element={<CreateAccount />} />
                  <Route path="/alldata/" element={<AllData />} />
            </Routes>
            </AuthStatus.Provider>
          </UserContext.Provider>
      </HashRouter>
  );
}

export default Spa;

