import React, {useState, useRef} from 'react';
import {Container, Nav, Navbar, OverlayTrigger, Overlay, Tooltip, Popover} from 'react-bootstrap'
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { UserContext } from '../Context/context';

import Logo from '../Pictures/bank.png';

function NavBar(props) {
    const ctx = React.useContext(UserContext);

    const [show, setShow] = useState(false);
    const [element, setElement] = useState();
    const target = useRef(null);


    const renderTooltip = (props) => (
        <Tooltip id="button-tooltip" {...props}>
        Simple tooltip
        </Tooltip>
    );

    const Pop = (props) => {

        return (
            <Overlay target={target.current} show={show} placement="right">
            {({ placement, arrowProps, child, show: _show, popper, ...props }) => (
            <div
                {...props}
                style={{
                position: 'absolute',
                backgroundColor: 'rgba(255, 100, 100, 0.85)',
                padding: '2px 10px',
                color: 'white',
                borderRadius: 3,
                ...props.style,
                }}
            >
             dsds

            </div>
            )}
        </Overlay>
        )

    };


    return(
        <Navbar expand="lg" bg="primary" variant="light">
            <Container>
                <Navbar.Brand href="/"><img src={Logo} width="50"></img></Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                      <OverlayTrigger placement="right" delay={{ show: 250, hide: 400 }} overlay={renderTooltip}>
                        <Nav.Link id="home" desc="This is our homepage..." href="/" >Home</Nav.Link>
                      </OverlayTrigger>
                            <Nav.Link id="home" desc="This is our homepage..." href="/" ref={target} onMouseOver={Pop()}>Home</Nav.Link>
                            <Nav.Link href="#/createaccount/">Create Account</Nav.Link>
                            <Nav.Link href="#/login/">Login</Nav.Link>
                            <Nav.Link href="#/deposit/">Deposit</Nav.Link>
                            <Nav.Link href="#/withdraw/">Withdrawal</Nav.Link>
                            <Nav.Link href="#/transactions/">Transactions</Nav.Link>
                            <Nav.Link href="#/alldata/">All Data</Nav.Link>

                    </Nav>
                    <Navbar.Collapse className="justify-content-end">
                            {
                            ctx.account[0].name > '' && <Navbar.Text>
                                Signed in as: <a href="#/transactions/"> {ctx.account[0].name}</a>
                            </Navbar.Text>
                            }
                    </Navbar.Collapse>
                </Navbar.Collapse>
            </Container>
        </ Navbar>

    )
}

export default NavBar;
