import React from 'react';
import {Card, Accordion, Button, Container, Table, TBody, Row, Col, Image} from "react-bootstrap";
import { BankForm } from '../Context/context'

function CreateAccount() {
    const [status, setStatus]   = React.useState('');

    return (
        <Card
        bgcolor="primary"
        header="Create Account"
        status={status}
        body={<BankForm
            bgcolor="primary"
            buttonLabel="Create Account"
            buttonAction="accCreate"
            showFormName={true}
            showFormEmail={true}
            showFormPassword={true}
            showFormAmount={false}
            amountType="none"
            successButton="Create another account"
            />}
        />
    )
    };

    export default CreateAccount
