import React from 'react';
import {Navbar, Button} from "react-bootstrap";
import {useLocation, useNavigate, useParams} from "react-router-dom";
import {UserContext, Consumer} from '../Context/context';

import Logo from '../Pictures/bank.png';
import { useEffect } from 'react';


function withRouter(Component) {
    function ComponentWithRouterProp(props) {
      let location = useLocation();
      let navigate = useNavigate();
      let params = useParams();
      return (
        <Component
          {...props}
          router={{ location, navigate, params }}
        />
      );
    }

    return ComponentWithRouterProp;
  }

function NavBar(props) {

    const location = props.router.location;

    document.getElementById(props.router.location.pathname).classList.add('active');

    return(
        <Navbar className="navbar navbar-expand-lg bg-light" activekey={location.pathname}>
        <div className="container-fluid">
            <a className="navbar-brand" href="#"><img src={Logo} className="img-fluid" alt="Responsive image" style={{maxWidth: "2rem"}} /></a>
             <Button className="navbar-toggler" type="Button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
             </Button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li id="/" className="nav-item">
                            <a className="nav-link" aria-current="page" href="/">Home</a>
                        </li>
                        <li id="/createaccount/" className="nav-item">
                            <a className="nav-link" href="#/createaccount/">Create Account</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#/login/">Login</a>
                        </li>
                        <li className="nav-item">
                            <a id="/deposit/" className="nav-link" href="#/deposit/">Deposit</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#/withdraw/">Withdraw</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#/transactions/">Transactions</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#/alldata/">All Data</a>
                        </li>
                    </ul>
                </div>
        </div>
        </ Navbar>
    // <Consumer>
    //         { value => (
    //         value.LoginStatus == true ? (
    //             <NavLoggedIn />
    //         ):(
    //         <NavPublic />
    //         )
    //         )}
    // </Consumer>
    )
}

export default withRouter(NavBar);
