import React from 'react';
import { Consumer, FormBody, Form } from '../Context/context';

function Withdraw() {
    const [status, setStatus]   = React.useState('');

    return (
        <Consumer>
            { value => (
            value.LoginStatus == true ? (
            <FormBody
            bgcolor="info"
            header="Withdraw"
            status={status}
            body={<Form
                bgcolor="info"
                buttonLabel="Withdraw"
                buttonAction="accWithdraw"
                showFormName={false}
                showFormEmail={false}
                showFormPassword={false}
                showFormAmount={true}
                amountType="none"
                successButton="Withdraw another amount!"
                />}
            />
            ):(
        <div className="container">
        <h1>You are currently not logged in!</h1><br/>
        <p>Please login or register for an account first...</p>
        </div>
            )
        )}
        </Consumer>
    )
};

export default Withdraw;
