import React, {useState, useRef} from 'react';
import {Container, Nav, Navbar, Overlay, Popover} from 'react-bootstrap'
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { UserContext } from '../Context/context';

import Logo from '../Pictures/bank.png';

function NavBar(props) {
    const ctx = React.useContext(UserContext);

    const popover = (props) => {

        return (
        <Popover class="popover" id={props.currentTarget.id}>
          <Popover.Header as="h3">{props.currentTarget.innerText}</Popover.Header>
          <Popover.Body>
          {props.currentTarget.attributes.desc.textContent}
          </Popover.Body>
        </Popover>
    )};

    return(
        <Navbar expand="lg" bg="primary" variant="light">
            <Container>
                <Navbar.Brand href="/"><img src={Logo} width="50"></img></Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                            <Nav.Link id="home" desc="This is our homepage..." href="/" onMouseOver={popover}>Home</Nav.Link>
                            <Nav.Link href="#/createaccount/">Create Account</Nav.Link>
                            <Nav.Link href="#/login/">Login</Nav.Link>
                            <Nav.Link href="#/deposit/">Deposit</Nav.Link>
                            <Nav.Link href="#/withdraw/">Withdrawal</Nav.Link>
                            <Nav.Link href="#/transactions/">Transactions</Nav.Link>
                            <Nav.Link href="#/alldata/">All Data</Nav.Link>

                    </Nav>
                    <Navbar.Collapse className="justify-content-end">
                            {
                            ctx.account[0].name > '' && <Navbar.Text>
                                Signed in as: <a href="#/transactions/"> {ctx.account[0].name}</a>
                            </Navbar.Text>
                            }
                    </Navbar.Collapse>
                </Navbar.Collapse>
            </Container>
        </ Navbar>

    )
}

export default NavBar;
