import React from 'react';
import {useEffect} from 'react';
import {Navbar, Button} from "react-bootstrap";
import {UserContext, AuthStatus} from '../Context/context';

import Logo from '../Pictures/bank.png';

function NavBar() {
    const ctx = React.useContext(UserContext)

   function render () { 
    useEffect(() => {
        const acc = React.useContext(AuthStatus);
        const isLoggedIn = acc.LoginStatus

        return (
        <Navbar className="navbar navbar-expand-lg bg-light">
        <div className="container-fluid">
        <a className="navbar-brand" href="#"><img src={Logo} className="img-fluid" alt="Responsive image" style={{maxWidth: "2rem"}} /></a>
        <Button className="navbar-toggler" type="Button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
        </Button>
        <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
            <li className="nav-item">
            <a className="nav-link active" aria-current="page" href="#">Home</a>
            </li>
            <li className="nav-item">
                <a className="nav-link" href="#/createaccount/">Create Account</a>
            </li>
            <li className="nav-item">
                <a className="nav-link" href="#/login/">Login</a>
            </li>
            {isLoggedIn && 
            <li className="nav-item">
                <a className="nav-link" href="#/deposit/">Deposit</a>
            </li>
            }
            <li className="nav-item">
                <a className="nav-link" href="#/withdraw/">Withdraw</a>
            </li>
            <li className="nav-item">
                <a className="nav-link" href="#/balance/">Balance</a>
            </li>
            <li className="nav-item">
                <a className="nav-link" href="#/alldata/">All Data</a>
            </li>
            </ul>
        </div>
        </div>
        </ Navbar>
      )
    }, [AuthStatus])
}
return (
    <render />
)
}

export default NavBar
