import React from 'react';
import {UserContext} from '../Context/context';

function Balance() {
    const ctx = React.useContext(UserContext);
    const [status, setStatus]   = React.useState('');
    const [isLoggedIn, setIsLoggedIn] = React.useState('');

    const users= ctx.users;
    const acc = ctx.account[0].email;

    for (let i = 0; i < users.length; i++) {
        const element = users[i];
        if (element.email === acc ){
            setStatus(`Account Balance for ${element.name}`)
            setIsLoggedIn('true')
            return userDetails = element;            
        } else {
            setStatus('Error: You are currently not logged in!')
            setIsLoggedIn('false')
        }
    };

    return (
        isLoggedIn ?

        <div className="container">
            <h1>{userDetails.name}'s Account Balance </h1><br/>
            <h5>{userDetails.balance}</h5>
        </div>
        :
        <div className="container">
        <h1>You are currently not logged in!</h1><br/>
        <p>Please login or register for an account first...</p>
    </div>
    );
};

export default Balance;
